/**
 * ppc_recomp.h - PowerPC Recompiler Compatibility Layer
 * 
 * Provides compatibility macros for code generated by static recompilers.
 */

#pragma once

#include <stdint.h>

// PowerPC register context (stub for compatibility)
typedef struct {
    uint32_t gpr[32];  // General purpose registers
    double fpr[32];    // Floating point registers
    uint32_t cr;       // Condition register
    uint32_t lr;       // Link register
    uint32_t ctr;      // Count register
} ppc_context_t;

// No-op macros for recompiler compatibility
#define PPC_FUNC_IMPL(name) 
#define PPC_CALL(addr)
#define PPC_LOAD_U32(addr) (*(uint32_t*)(addr))
#define PPC_STORE_U32(addr, val) (*(uint32_t*)(addr) = (val))

